mapper.update:
UPDATE post
SET renew_status = true
WHERE user_no = #{userNo}
  AND renew_status = false
  AND transaction_date > (SELECT renew_time FROM user WHERE user_no = #{userNo})
  AND transaction_date <= #{now}

mapper.select:
SELECT DATE(transaction_date) as transaction_date, category, user_no, amount
FROM post
WHERE user_no = #{userNo}
  AND renew_status = false
  AND transaction_date > (SELECT renew_time FROM user WHERE user_no = #{userNo})
  AND transaction_date <= #{now}

mapper.update:
UPDATE post
SET public_status = NOT public_status
WHERE post_no = #{postNo}

mapper.select:
SELECT img_url
FROM post_img
WHERE post_no = #{no}

mapper.update:
UPDATE post
SET great_count = great_count + 1
WHERE post_no = #{postNo}

UPDATE post
SET stupid_count = stupid_count + 1
WHERE post_no = #{postNo}

UPDATE post
SET great_count = great_count - 1
WHERE post_no = #{postNo}

UPDATE post
SET stupid_count = stupid_count - 1
WHERE post_no = #{postNo}
